import React, { useState } from "react";
import "../App.css";
import ToDoForm from "./ToDoForm";
import ToDo from "./ToDo";
// save data in local storage..
const ToDoWarpper = () => {
  const [idCounter, setIdCounter] = React.useState(0); // id method
  const [todos, setTodos] = React.useState([]);
  const [doneTodo, setDonetodo] = useState([]);
  const [deletedTask, setdeletedTask] = React.useState([]);

  const formattedDefultDate = ` ${new Date().toLocaleString("default", {
    month: "short",
  })} ${String(new Date().getDate()).padStart(2, "0")}`;

  const addTodo = (text, date) => {
    const newDate = new Date(date);
    const formattedDate = `${newDate.toLocaleString("default", {
      month: "short",
    })} ${String(newDate.getDate()).padStart(2, "0")}`;

    setTodos([
      ...todos,
      {
        id: idCounter,
        task: text === "" ? "un Titled" : text,
        dueDate: date === "" ? formattedDefultDate : formattedDate,
        completed: false,
        isEditing: false,
      },
    ]);
    setIdCounter(idCounter + 1);
    saveDataInLocalStorge(todos, doneTodo, deletedTask);
  };
  console.log(todos);
  const deleteMethod = (id) => {
    setTodos(todos.filter((todo) => todo.id !== id));
    const deleteFitter = todos.find((todo) => todo.id === id);
    setdeletedTask([...deletedTask, deleteFitter]);
    saveDataInLocalStorge(todos, doneTodo, deletedTask);
  };
  const doneMethod = (id) => {
    setTodos(todos.filter((todo) => todo.id !== id));
    const doneFitter = todos.find((todo) => todo.id === id);
    // retrieve the first element in an array that satisfies a specific condition.
    setDonetodo([...doneTodo, doneFitter]);
    saveDataInLocalStorge(todos, doneTodo, deletedTask);
  };
  const reTodo = (id, array) => {
    setDonetodo(doneTodo.filter((todo) => todo.id !== id));
    const findtodo = doneTodo.find((todo) => todo.id === id);
    setTodos([...todos, findtodo]);
    saveDataInLocalStorge(todos, doneTodo, deletedTask);
  };

  function saveDataInLocalStorge(taskArray, doneArray, deleteArray) {
    window.localStorage.setItem("tasks", JSON.stringify(taskArray));
    window.localStorage.setItem("doneTasks", JSON.stringify(doneArray));
    window.localStorage.setItem("deletedTasks", JSON.stringify(deleteArray));
  }

  function getDataFromLocalStorge() {
    if (localStorage.getItem("tasks") !== null) {
      let task = window.localStorage.getItem("tasks");
      let tasks = JSON.parse(task);
      return <ToDoWarpper />;
    }
  }

  return (
    <div>
      <div className="TodoWrapper">
        <h1>To Do List</h1>
        <ToDoForm addTodo={addTodo} deletedTodo={deletedTask} />
        {todos.map((todo, index) => (
          <ToDo
            key={index}
            task={todo}
            deleteMethod={deleteMethod}
            doneMethod={doneMethod}
            completed={false}
            reToDo={reTodo}
          />
        ))}
      </div>
      <div className="TodoWrapper">
        <h1> Done Tasks</h1>
        {doneTodo.map((todo, index) => (
          <ToDo
            key={index}
            task={todo}
            completed={true}
            reToDo={reTodo}
            fun={"done"}
          />
        ))}
      </div>
      <div className="TodoWrapper">
        <h1> Deleted Tasks</h1>
        {deletedTask.map((todo, index) => (
          <ToDo key={index} task={todo} completed={true} fun={"delete"} />
        ))}
      </div>
    </div>
  );
};

export default ToDoWarpper;
