import { faPenToSquare, faTrash } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import React from "react";

const ToDo = (props) => {
  //props.reTodo(props.task.id)

  if (props.completed !== true) {
    return (
      <div>
        <div className="Todo">
          <input
            onClick={() => props.doneMethod(props.task.id)}
            type="checkbox"
            style={{ width: "30px", height: "19px", backgroundColor: "red" }}
          />
          <p>{props.task.dueDate}</p>
          <p>{props.task.task}</p>
          <div>
            <FontAwesomeIcon icon={faPenToSquare} />
            <FontAwesomeIcon
              icon={faTrash}
              onClick={() => props.deleteTodo(props.task.id)}
            />
          </div>
        </div>
      </div>
    );
  } else if (props.fun === "delete") {
    return (
      <div>
        <div
          className="Todo-done"
          style={{ display: "flex", justifyContent: "center" }}
        >
          <p>{props.task.dueDate}</p>
          <p>{props.task.task}</p>
        </div>
      </div>
    );
  } else {
    return (
      <div>
        <div className="Todo-done">
          <input
            onClick={() => props.reToDo(props.task.id)}
            type="checkbox"
            checked="checked"
            style={{ width: "30px", height: "19px", backgroundColor: "red" }}
          />
          <p>{props.task.dueDate}</p>
          <p>{props.task.task}</p>
        </div>
      </div>
    );
  }
};

export default ToDo;
