import React, { useState } from "react";
import "../App.css";
import ToDoForm from "./ToDoForm";
import ToDo from "./ToDo";
// save data in local storage..
const ToDoWarpper = () => {
  const [idCounter, setIdCounter] = React.useState(0); // id method
  const [todos, setTodos] = React.useState([]); // state hook to save info of task

  const formattedDefultDate = ` ${new Date().toLocaleString("default", {
    month: "short",
  })} ${String(new Date().getDate()).padStart(2, "0")}`;

  const addTodo = (text, date) => {
    const newDate = new Date(date);
    const formattedDate = `${newDate.toLocaleString("default", {
      month: "short",
    })} ${String(newDate.getDate()).padStart(2, "0")}`;

    setTodos([
      ...todos,
      {
        id: idCounter,
        task: text === "" ? "un Titled" : text,
        dueDate: date === "" ? formattedDefultDate : formattedDate,
        completed: false,
        isEditing: false,
      },
    ]);
    setIdCounter(idCounter + 1);
  };

  //add delted task in array
  const [deletedTask, setdeletedTask] = React.useState([]);

  const deleteTodo = (id) => {
    setTodos(todos.filter((todo) => todo.id !== id));
    const deleteFitter = todos.find((todo) => todo.id === id);
    // retrieve the first element in an array that satisfies a specific condition.
    setdeletedTask([...deletedTask, deleteFitter]);
  };

  const doneTodo = useState([]);
  const doneMethod = () => {};
  return (
    <div className="TodoWrapper">
      <h1>To Do List</h1>
      <ToDoForm addTodo={addTodo} />
      {todos.map((todo, index) => (
        <ToDo
          key={index}
          task={todo}
          deleteTodo={deleteTodo}
          doneTodo={doneMethod}
        />
      ))}
    </div>
  );
};

export default ToDoWarpper;
