import React, { useEffect, useState } from "react";
import "../App.css";
import ToDoForm from "./ToDoForm";
import ToDo from "./ToDo";
// save data in local storage..
const ToDoWarpper = () => {
  let [idCounter, setIdCounter] = React.useState(0); // id method
  let [todos, setTodos] = React.useState([]);
  let [doneTodo, setDonetodo] = useState([]);
  let [deletedTodo, setdeletedTodo] = React.useState([]);

  // useEffect(() => {
  //   setdeletedTodo(JSON.parse(localStorage.getItem("deletedTodos") || []));
  // }, []);

  // useEffect(() => {
  //   setDonetodo(JSON.parse(localStorage.getItem("doneTasks") || []));
  // }, []);

  // useEffect(() => {
  //   setdeletedTodo(JSON.parse(localStorage.getItem("deletedTodos") || []));
  // }, []);

  const formattedDefultDate = ` ${new Date().toLocaleString("default", {
    month: "short",
  })} ${String(new Date().getDate()).padStart(2, "0")}`;

  const addTodo = (text, date) => {
    const newDate = new Date(date);
    const formattedDate = `${newDate.toLocaleString("default", {
      month: "short",
    })} ${String(newDate.getDate()).padStart(2, "0")}`;

    setTodos([
      ...todos,
      {
        id: idCounter,
        task: text === "" ? "un Titled" : text,
        dueDate: date === "" ? formattedDefultDate : formattedDate,
        completed: false,
        isEditing: false,
      },
    ]);
    setIdCounter(idCounter + 1);
    saveDataInLocalStorge(todos, doneTodo, deletedTodo);
  };

  //short it..
  const deleteMethod = (id) => {
    let deleteFitter;
    setTodos(
      todos.filter((todo) => {
        if (todo.id === id) {
          deleteFitter = todo;
        }
        return todo.id !== id;
      })
    );

    deleteFitter.completed = true;

    setdeletedTodo([...deletedTodo, deleteFitter]);
    saveDataInLocalStorge(todos, doneTodo, deletedTodo);
  };
  const doneMethod = (id) => {
    setTodos(todos.filter((todo) => todo.id !== id));
    const doneFitter = todos.find((todo) => todo.id === id);
    doneFitter.completed = true;
    setDonetodo([...doneTodo, doneFitter]);
    saveDataInLocalStorge(todos, doneTodo, deletedTodo);
  };
  const reTodo = (id) => {
    setDonetodo(doneTodo.filter((todo) => todo.id !== id));
    const findtodo = doneTodo.find((todo) => todo.id === id);
    setTodos([...todos, findtodo]);
    saveDataInLocalStorge(todos, doneTodo, deletedTodo);
  };

  // const completedMethod = (id) => {
  //   setTodos(
  //     todos.map((todo) =>
  //       todo.id === id ? { ...todo, completed: !todo.completed } : todo
  //     )
  //   );
  // };

  function saveDataInLocalStorge(taskArray, doneArray, deleteArray) {
    localStorage.setItem("tasks", JSON.stringify(taskArray));
    localStorage.setItem("doneTasks", JSON.stringify(doneArray));
    localStorage.setItem("deletedTodos", JSON.stringify(deleteArray));
  }

  // console.log(todos);
  // console.log(doneTodo);
  // console.log(deletedTodo);
  return (
    <div>
      <div className="TodoWrapperContainer">
        <div className="TodoWrapper">
          <h1>To Do List</h1>
          <ToDoForm addTodo={addTodo} deletedTodo={deletedTodo} />
          {todos.map((todo, index) => (
            <ToDo
              key={index}
              task={todo}
              deleteMethod={deleteMethod}
              doneMethod={doneMethod}
              reToDo={reTodo}
            />
          ))}
        </div>
        <div className="TodoWrapper">
          <h1> Done Tasks</h1>
          {doneTodo.map((todo, index) => (
            <ToDo key={index} task={todo} reToDo={reTodo} fun={"done"} />
          ))}
        </div>
        <div className="TodoWrapper">
          <h1> Deleted Tasks</h1>
          {deletedTodo.map((todo, index) => (
            <ToDo key={index} task={todo} fun={"delete"} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default ToDoWarpper;
