import React from "react";
import "../App.css";
import ToDoForm from "./ToDoForm";

const ToDoWarpper = () => {
  const [idCounter, setIdCounter] = React.useState(0); // id method
  const [todos, setTodos] = React.useState([]);

  const addTodo = (todo) => {
    if (todos === "") {
      setTodos([
        { id: idCounter, task: todo, completed: false, isEditing: false },
      ]);
    } else {
      setTodos([
        ...todos,
        { id: idCounter, task: todo, completed: false, isEditing: false },
      ]);
    }

    setIdCounter(idCounter + 1);
    console.log(todos);
  };

  return (
    <div className="TodoWrapper">
      <ToDoForm addTodo={addTodo} />
    </div>
  );
};

export default ToDoWarpper;
