import React from "react";
import "../App.css";
import ToDoForm from "./ToDoForm";

const ToDoWarpper = () => {
  const [idCounter, setIdCounter] = React.useState(0); // id method
  const [todos, setTodos] = React.useState([]); // state hook to save info of task
  const id = 0;
  // console.log(todos);
  // console.log(typeof todos);
  const addTodo = (todo) => {
    if (todos === "") {
      setTodos([
        {
          id: 0,
          task: todo === "" ? "unTiltled" : todo,
          completed: false,
          isEditing: false,
        },
      ]);
    } else {
      setTodos([
        ...todos,
        {
          id: id + 1,
          task: todo === "" ? "unTiltled" : todo,
          completed: false,
          isEditing: false,
        },
      ]);

      setIdCounter(idCounter + 1);
    }
    console.log(todos);

    // setIdCounter(idCounter + 1);
  };

  return (
    <div className="TodoWrapper">
      <ToDoForm addTodo={addTodo} />
    </div>
  );
};

export default ToDoWarpper;
