import React from "react";
import "../App.css";
import ToDoForm from "./ToDoForm";
import ToDo from "./ToDo";
// save data in local storage..
const ToDoWarpper = () => {
  const [idCounter, setIdCounter] = React.useState(0); // id method
  const [todos, setTodos] = React.useState([]); // state hook to save info of task
  const addTodo = (todo) => {
    setTodos([
      ...todos,
      {
        id: idCounter,
        task: todo === "" ? "unTiltled" : todo,
        completed: false,
        isEditing: false,
      },
    ]);
    setIdCounter(idCounter + 1);
  };

  const removeTodo = (todo) => {
    if (todos.id === todo)
      setTodos([
        ...todos,
        {
          id: idCounter,
          task: todo === "" ? "unTiltled" : todo,
          completed: false,
          isEditing: false,
        },
      ]);
  };
  return (
    <div className="TodoWrapper">
      <ToDoForm addTodo={addTodo} />
      {todos.map((todo, index) => (
        <ToDo
          id={todo.id}
          task={todo.task}
          completed={todo.completed}
          isEditing={todo.isEditing}
          removedElement={removeTodo}
        />
      ))}
    </div>
  );
};

export default ToDoWarpper;
