import React from "react";
import "../App.css";
import ToDoForm from "./ToDoForm";

const ToDoWarpper = () => {
  const [todos, setTodos] = React.useState([]);
  const [idCounter, setId] = React.useState(0);

  const addTodo = (todo) => {
    setTodos([...todos, { id: idCounter }]);
  };
  idCounter;
  return (
    <div className="TodoWrapper">
      <ToDoForm />
    </div>
  );
};

export default ToDoWarpper;
